import React, { useEffect, useState } from 'react';
import { supabase } from './supabaseClient';

const FILTROS_INICIALES = {
  tipo_arma: '',
  marca: '',
  calibre: '',
  estado_arma: '',
  empadronamiento: '',
  precio_orden: '', // 'asc' o 'desc'
  busqueda: '',
};

function IndexArmas() {
  const [armas, setArmas] = useState([]);
  const [filtros, setFiltros] = useState(FILTROS_INICIALES);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchArmas();
    // eslint-disable-next-line
  }, [filtros]);

  const fetchArmas = async () => {
    setLoading(true);
    let query = supabase
      .from('armas')
      .select('*')
      .in('estado_publicacion', ['PUBLICADA_SIN_CONTACTO', 'PUBLICADA_CON_CONTACTO']);
    if (filtros.tipo_arma) query = query.eq('tipo_arma', filtros.tipo_arma);
    if (filtros.marca) query = query.ilike('marca', `%${filtros.marca}%`);
    if (filtros.calibre) query = query.ilike('calibre', `%${filtros.calibre}%`);
    if (filtros.estado_arma) query = query.eq('estado_arma', filtros.estado_arma);
    if (filtros.empadronamiento) query = query.eq('empadronamiento', filtros.empadronamiento);
    if (filtros.busqueda) {
      query = query.or(`nombre.ilike.%${filtros.busqueda}%,modelo.ilike.%${filtros.busqueda}%`);
    }
    if (filtros.precio_orden) {
      query = query.order('precio_venta', { ascending: filtros.precio_orden === 'asc' });
    } else {
      query = query.order('fecha_creacion', { ascending: false });
    }
    const { data, error } = await query;
    if (error) setError(error.message);
    else setArmas(data);
    setLoading(false);
  };

  const handleFiltro = (e) => {
    const { name, value } = e.target;
    setFiltros(f => ({ ...f, [name]: value }));
  };

  return (
    <div style={{maxWidth:1100,margin:'40px auto'}}>
      <h2>Armas publicadas</h2>
      <div style={{display:'flex',gap:16,flexWrap:'wrap',marginBottom:16}}>
        <input name="busqueda" placeholder="Buscar por nombre o modelo" value={filtros.busqueda} onChange={handleFiltro} />
        <input name="marca" placeholder="Marca" value={filtros.marca} onChange={handleFiltro} />
        <input name="calibre" placeholder="Calibre" value={filtros.calibre} onChange={handleFiltro} />
        <select name="tipo_arma" value={filtros.tipo_arma} onChange={handleFiltro}>
          <option value="">Tipo de arma</option>
          <option value="Pistola">Pistola</option>
          <option value="Revolver">Revolver</option>
          <option value="Fusil">Fusil</option>
          <option value="Carabina">Carabina</option>
          <option value="Escopeta">Escopeta</option>
          <option value="Mira telescopica">Mira telescopica</option>
          <option value="Otro">Otro</option>
        </select>
        <select name="estado_arma" value={filtros.estado_arma} onChange={handleFiltro}>
          <option value="">Estado</option>
          <option value="Nueva">Nueva</option>
          <option value="Usada">Usada</option>
        </select>
        <select name="empadronamiento" value={filtros.empadronamiento} onChange={handleFiltro}>
          <option value="">Empadronamiento</option>
          <option value="Reempadronada">Reempadronada</option>
          <option value="En tramite ANMAC">En tramite ANMAC</option>
          <option value="Tenencia Vigente">Tenencia Vigente</option>
          <option value="Tenencia Vencida">Tenencia Vencida</option>
          <option value="En venta NUEVA">En venta NUEVA</option>
        </select>
        <select name="precio_orden" value={filtros.precio_orden} onChange={handleFiltro}>
          <option value="">Ordenar por</option>
          <option value="asc">Precio: menor a mayor</option>
          <option value="desc">Precio: mayor a menor</option>
        </select>
      </div>
      {loading && <p>Cargando armas...</p>}
      {error && <p style={{color:'red'}}>{error}</p>}
      <div style={{display:'flex',flexWrap:'wrap',gap:24}}>
        {armas.map(arma => (
          <div key={arma.id} style={{border:'1px solid #ccc',borderRadius:8,padding:12,width:320,background:'#fafafa'}}>
            <b>{arma.nombre}</b> <br/>
            <span>{arma.marca} {arma.modelo}</span><br/>
            <span>Tipo: {arma.tipo_arma} | Calibre: {arma.calibre}</span><br/>
            <span>Estado: {arma.estado_arma}</span><br/>
            <span>Precio: {arma.precio_venta} {arma.moneda}</span><br/>
            <span>Empadronamiento: {arma.empadronamiento}</span><br/>
            {arma.fotos && arma.fotos.length > 0 && (
              <img src={arma.fotos[0]} alt="foto" width={200} style={{margin:'8px 0',borderRadius:4}} />
            )}
            {arma.estado_publicacion === 'PUBLICADA_CON_CONTACTO' && (
              <div style={{marginTop:8,fontSize:14}}>
                <b>Contacto:</b><br/>
                {arma.nombre}<br/>
                {arma.telefono}<br/>
                {arma.email}<br/>
                {arma.ciudad}, {arma.provincia}, {arma.pais}
              </div>
            )}
            {arma.estado_publicacion === 'PUBLICADA_SIN_CONTACTO' && (
              <div style={{marginTop:8,fontSize:14,color:'#888'}}>Contacto visible tras pago</div>
            )}
            {arma.estado_publicacion === 'VENDIDA' && (
              <div style={{marginTop:8,fontWeight:'bold',color:'red'}}>VENDIDO</div>
            )}
            <details>
              <summary>Ver más</summary>
              <div style={{fontSize:13}}>
                <b>N° Serie:</b> {arma.numero_serie}<br/>
                <b>Comentarios:</b> {arma.comentarios}
                {arma.fotos && arma.fotos.length > 1 && (
                  <div style={{display:'flex',gap:4,marginTop:4}}>
                    {arma.fotos.slice(1).map((url,i) => <img key={i} src={url} alt="foto" width={60} />)}
                  </div>
                )}
              </div>
            </details>
          </div>
        ))}
      </div>
    </div>
  );
}

export default IndexArmas;
