import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { supabase } from './supabaseClient';
import Navbar from './Navbar';

const styles = {
  page: { 
    width: '100vw', 
    minHeight: '100vh', 
    background: '#f8f9fa', 
    color: '#333', 
    boxSizing: 'border-box', 
    padding: 0, 
    margin: 0, 
    overflowX: 'hidden' 
  },
  container: {
    maxWidth: 1200,
    margin: '0 auto',
    padding: '100px 20px 40px',
    background: '#fff',
    minHeight: '100vh'
  },
  backBtn: {
    position: 'absolute',
    top: 80,
    left: 20,
    backgroundColor: '#223a5e',
    color: '#fff',
    border: 'none',
    borderRadius: 6,
    padding: '8px 16px',
    cursor: 'pointer',
    fontSize: 14,
    zIndex: 10
  },
  mainContent: {
    display: 'grid',
    gridTemplateColumns: '1.2fr 1fr',
    gap: 40,
    marginBottom: 40
  },
  mobileContent: {
    display: 'flex',
    flexDirection: 'column',
    gap: 20
  },
  imageSection: {
    display: 'flex',
    flexDirection: 'column'
  },
  mainImage: {
    width: '100%',
    height: 500,
    objectFit: 'contain',
    backgroundColor: '#fff',
    border: '2px solid #ffc107',
    borderRadius: 12,
    marginBottom: 16,
    cursor: 'zoom-in'
  },
  mobileImage: {
    width: '100%',
    height: 300,
    objectFit: 'contain',
    backgroundColor: '#fff',
    border: '2px solid #ffc107',
    borderRadius: 12,
    marginBottom: 16,
    cursor: 'zoom-in'
  },
  thumbnails: {
    display: 'flex',
    gap: 8,
    flexWrap: 'wrap',
    justifyContent: 'center'
  },
  thumbnail: {
    width: 80,
    height: 60,
    objectFit: 'contain',
    backgroundColor: '#fff',
    border: '2px solid #ddd',
    borderRadius: 6,
    cursor: 'pointer',
    transition: 'border-color 0.2s'
  },
  thumbnailActive: {
    borderColor: '#ffc107'
  },
  infoSection: {
    display: 'flex',
    flexDirection: 'column',
    gap: 20
  },
  title: {
    fontSize: 32,
    fontWeight: 700,
    color: '#ffc107',
    marginBottom: 16,
    textShadow: '1px 1px 2px rgba(0,0,0,0.1)'
  },
  mobileTitle: {
    fontSize: 24,
    fontWeight: 700,
    color: '#ffc107',
    marginBottom: 16,
    textAlign: 'center',
    textShadow: '1px 1px 2px rgba(0,0,0,0.1)'
  },
  price: {
    fontSize: 28,
    fontWeight: 700,
    color: '#223a5e',
    marginBottom: 16
  },
  statusBadge: {
    display: 'inline-block',
    padding: '6px 12px',
    borderRadius: 20,
    fontSize: 14,
    fontWeight: 600,
    marginBottom: 20
  },
  statusNew: {
    backgroundColor: '#28a745',
    color: '#fff'
  },
  statusUsed: {
    backgroundColor: '#ffc107',
    color: '#000'
  },
  statusSold: {
    backgroundColor: '#dc3545',
    color: '#fff'
  },
  specs: {
    display: 'grid',
    gridTemplateColumns: '1fr 1fr',
    gap: 12,
    marginBottom: 20
  },
  mobileSpecs: {
    display: 'flex',
    flexDirection: 'column',
    gap: 8,
    marginBottom: 20
  },
  specItem: {
    display: 'flex',
    padding: '8px 12px',
    backgroundColor: '#f8f9fa',
    borderRadius: 6,
    border: '1px solid #e9ecef'
  },
  specLabel: {
    fontWeight: 600,
    marginRight: 8,
    color: '#495057',
    minWidth: 80
  },
  specValue: {
    color: '#6c757d'
  },
  contactCard: {
    background: 'linear-gradient(135deg, #223a5e 0%, #2c5282 100%)',
    color: '#fff',
    padding: 24,
    borderRadius: 12,
    marginTop: 20,
    boxShadow: '0 4px 12px rgba(34, 58, 94, 0.3)'
  },
  contactTitle: {
    fontSize: 20,
    fontWeight: 700,
    marginBottom: 16,
    color: '#ffc107',
    textAlign: 'center'
  },
  contactInfo: {
    fontSize: 16,
    lineHeight: 1.8,
    textAlign: 'center'
  },
  contactButtons: {
    display: 'flex',
    gap: 12,
    marginTop: 16,
    justifyContent: 'center'
  },
  callBtn: {
    backgroundColor: '#28a745',
    color: '#fff',
    border: 'none',
    padding: '12px 20px',
    borderRadius: 8,
    fontSize: 16,
    fontWeight: 600,
    cursor: 'pointer',
    textDecoration: 'none',
    display: 'flex',
    alignItems: 'center',
    gap: 8
  },
  emailBtn: {
    backgroundColor: '#ffc107',
    color: '#000',
    border: 'none',
    padding: '12px 20px',
    borderRadius: 8,
    fontSize: 16,
    fontWeight: 600,
    cursor: 'pointer',
    textDecoration: 'none',
    display: 'flex',
    alignItems: 'center',
    gap: 8
  },
  description: {
    backgroundColor: '#f8f9fa',
    padding: 20,
    borderRadius: 8,
    marginTop: 20,
    border: '1px solid #e9ecef'
  },
  descriptionTitle: {
    fontSize: 18,
    fontWeight: 600,
    marginBottom: 12,
    color: '#495057'
  },
  descriptionText: {
    lineHeight: 1.6,
    color: '#6c757d'
  },
  noContactCard: {
    background: 'linear-gradient(135deg, #ffc107 0%, #ffb300 100%)',
    color: '#000',
    padding: 24,
    borderRadius: 12,
    marginTop: 20,
    textAlign: 'center',
    fontWeight: 600,
    fontSize: 16
  }
};



function DetalleArma() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [arma, setArma] = useState(null);
  const [loading, setLoading] = useState(true);
  const [fotoIdx, setFotoIdx] = useState(0);
  const [modalOpen, setModalOpen] = useState(false);

  useEffect(() => {
    const fetchArma = async () => {
      const { data } = await supabase.from('armas').select('*').eq('id', id).single();
      setArma(data);
      setLoading(false);
    };
    fetchArma();
  }, [id]);

  if (loading) return (
    <div>
      <Navbar />
      <div style={{textAlign:'center', marginTop: 150, color: '#666'}}>Cargando...</div>
    </div>
  );

  if (!arma) return (
    <div>
      <Navbar />
      <div style={{textAlign:'center', marginTop: 150, color: '#dc3545'}}>No se encontró el arma.</div>
    </div>
  );

  const fotos = arma.fotos || [];
  const isMobile = window.innerWidth <= 768;

  const handleContactClick = () => {
    if (arma.telefono) {
      window.open(`tel:${arma.telefono}`);
    }
  };

  const handleEmailClick = () => {
    if (arma.email) {
      window.open(`mailto:${arma.email}`);
    }
  };

  return (
    <>
      <Navbar />
      <div style={styles.page}>
        <button style={styles.backBtn} onClick={() => navigate(-1)}>
          ← Volver
        </button>

        <div style={styles.container}>
          {/* Breadcrumb */}
          <div style={styles.breadcrumb}>
            <span 
              style={styles.breadcrumbLink} 
              onClick={() => navigate('/')}
            >
              VentaDeArmas
            </span>
            <span>/</span>
            <span 
              style={styles.breadcrumbLink}
              onClick={() => navigate('/listado')}
            >
              Armas
            </span>
            <span>/</span>
            <span>{arma.tipo_arma}</span>
            <span>/</span>
            <span>{arma.marca} {arma.modelo}</span>
          </div>

          <div style={isMobile ? {display: 'flex', flexDirection: 'column', gap: 20} : styles.mainContent}>
            {/* Sección de imágenes */}
            <div style={styles.imageSection}>
              {fotos.length > 0 && (
                <>
                  <img
                    src={fotos[fotoIdx]}
                    alt={`${arma.marca} ${arma.modelo}`}
                    style={styles.mainImage}
                    onClick={() => setModalOpen(true)}
                  />
                  {fotos.length > 1 && (
                    <div style={styles.thumbnails}>
                      {fotos.map((foto, idx) => (
                        <img
                          key={idx}
                          src={foto}
                          alt={`Vista ${idx + 1}`}
                          style={{
                            ...styles.thumbnail,
                            ...(idx === fotoIdx ? styles.thumbnailActive : {})
                          }}
                          onClick={() => setFotoIdx(idx)}
                        />
                      ))}
                    </div>
                  )}
                </>
              )}
            </div>

            {/* Información del producto */}
            <div style={styles.productInfo}>
              <h1 style={styles.title}>
                {arma.marca} {arma.modelo}
              </h1>

              <div style={styles.sku}>
                SKU: ARM{arma.id.toString().padStart(4, '0')}
              </div>

              <div style={styles.condition}>
                {arma.estado_arma === 'Nueva' ? 'NUEVA' : 'USADA'}
              </div>

              {/* Rating simulado */}
              <div style={styles.rating}>
                <span>⭐⭐⭐⭐⭐</span>
                <span style={{color: '#28a745', fontSize: 14}}>Excelente</span>
              </div>

              <div style={styles.price}>
                {arma.moneda === 'USD' ? 'US$' : '$'}{arma.precio_venta}
              </div>

              <div style={styles.inStock}>
                En stock
              </div>

              {/* Botones de acción */}
              {arma.estado_publicacion === 'VENDIDA' ? (
                <div style={styles.soldOut}>
                  VENDIDO
                </div>
              ) : arma.estado_publicacion === 'PUBLICADA_CON_CONTACTO' ? (
                <div style={styles.actionButtons}>
                  <button style={styles.addToCartBtn} onClick={handleContactClick}>
                    CONTACTAR VENDEDOR
                  </button>
                  <button style={styles.makeOfferBtn} onClick={handleEmailClick}>
                    HACER OFERTA
                  </button>
                </div>
              ) : (
                <div style={{...styles.actionButtons, flexDirection: 'column'}}>
                  <div style={{...styles.addToCartBtn, backgroundColor: '#ffc107', color: '#000'}}>
                    CONTACTO DISPONIBLE TRAS PAGO
                  </div>
                </div>
              )}

              <div style={styles.shippingInfo}>
                📍 Ubicación: {arma.ciudad}, {arma.provincia}
              </div>

              {/* Promise section */}
              <div style={styles.promise}>
                <div style={styles.promiseIcon}>
                  ✓
                </div>
                <div style={styles.promiseContent}>
                  <div style={styles.promiseTitle}>GARANTÍA VENTADEARMAS</div>
                  <div style={styles.promiseText}>
                    Verificamos cada publicación para brindarte la mejor experiencia de compra. 
                    Soporte completo durante todo el proceso.
                  </div>
                </div>
              </div>

              {/* Social share */}
              <div style={styles.socialShare}>
                <button style={styles.socialBtn} title="Facebook">f</button>
                <button style={styles.socialBtn} title="Twitter">t</button>
                <button style={styles.socialBtn} title="WhatsApp">w</button>
                <button style={styles.socialBtn} title="Email">@</button>
              </div>
            </div>
          </div>

          {/* Especificaciones técnicas */}
          <div style={styles.specsSection}>
            <h2 style={styles.sectionTitle}>INFORMACIÓN DEL PRODUCTO Y ESPECIFICACIONES</h2>

            {/* Descripción del vendedor */}
            {arma.comentarios && (
              <div style={{marginBottom: 30}}>
                <h3 style={{fontSize: 18, fontWeight: 600, marginBottom: 10, color: '#333'}}>
                  DESCRIPCIÓN DEL VENDEDOR
                </h3>
                <div style={{
                  padding: 16,
                  backgroundColor: '#f8f9fa',
                  borderRadius: 8,
                  border: '1px solid #ddd',
                  lineHeight: 1.6,
                  color: '#666'
                }}>
                  {arma.comentarios}
                </div>
              </div>
            )}

            {/* Tabla de especificaciones */}
            <h3 style={{fontSize: 18, fontWeight: 600, marginBottom: 15, color: '#333'}}>
              INFORMACIÓN GENERAL DEL FABRICANTE
            </h3>

            <table style={styles.specsTable}>
              <tbody>
                <tr style={styles.specRow}>
                  <td style={styles.specLabel}>Tipo</td>
                  <td style={styles.specValue}>{arma.tipo_arma}</td>
                </tr>
                <tr style={styles.specRow}>
                  <td style={styles.specLabel}>Marca</td>
                  <td style={styles.specValue}>{arma.marca}</td>
                </tr>
                <tr style={styles.specRow}>
                  <td style={styles.specLabel}>Modelo</td>
                  <td style={styles.specValue}>{arma.modelo}</td>
                </tr>
                <tr style={styles.specRow}>
                  <td style={styles.specLabel}>Calibre</td>
                  <td style={styles.specValue}>{arma.calibre}</td>
                </tr>
                <tr style={styles.specRow}>
                  <td style={styles.specLabel}>Estado</td>
                  <td style={styles.specValue}>{arma.estado_arma}</td>
                </tr>
                <tr style={styles.specRow}>
                  <td style={styles.specLabel}>Empadronamiento</td>
                  <td style={styles.specValue}>{arma.empadronamiento}</td>
                </tr>
                <tr style={styles.specRow}>
                  <td style={styles.specLabel}>Número de Serie</td>
                  <td style={styles.specValue}>{arma.numero_serie}</td>
                </tr>
                <tr style={styles.specRow}>
                  <td style={styles.specLabel}>País</td>
                  <td style={styles.specValue}>{arma.pais}</td>
                </tr>
              </tbody>
            </table>
          </div>

          {/* Información de contacto */}
          {arma.estado_publicacion === 'PUBLICADA_CON_CONTACTO' && (
            <div style={styles.contactSection}>
              <div style={styles.contactTitle}>INFORMACIÓN DE CONTACTO</div>
              <div style={styles.contactInfo}>
                <strong>Vendedor:</strong> {arma.nombre}<br/>
                <strong>Teléfono:</strong> {arma.telefono}<br/>
                <strong>Email:</strong> {arma.email}<br/>
                <strong>Ubicación:</strong> {arma.ciudad}, {arma.provincia}, {arma.pais}
              </div>
            </div>
          )}
        </div>

        {/* Modal para imagen ampliada */}
        {modalOpen && fotos.length > 0 && (
          <div 
            style={{
              position: 'fixed',
              top: 0,
              left: 0,
              right: 0,
              bottom: 0,
              backgroundColor: 'rgba(0,0,0,0.9)',
              zIndex: 1000,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              padding: 20
            }}
            onClick={() => setModalOpen(false)}
          >
            <div style={{position: 'relative', maxWidth: '90vw', maxHeight: '90vh'}}>
              <img
                src={fotos[fotoIdx]}
                alt={`${arma.marca} ${arma.modelo}`}
                style={{
                  maxWidth: '100%',
                  maxHeight: '100%',
                  objectFit: 'contain',
                  borderRadius: 8
                }}
                onClick={(e) => e.stopPropagation()}
              />
              <button
                style={{
                  position: 'absolute',
                  top: -40,
                  right: 0,
                  background: '#fff',
                  border: 'none',
                  borderRadius: '50%',
                  width: 32,
                  height: 32,
                  cursor: 'pointer',
                  fontSize: 18,
                  fontWeight: 'bold'
                }}
                onClick={() => setModalOpen(false)}
              >
                ×
              </button>
            </div>
          </div>
        )}
      </div>
    </>
  );
}

export default DetalleArma;
