import './App.css';
import FormularioArma from './FormularioArma';
import IndexArmas from './IndexArmas';
import Navbar from './Navbar';
import avisoLegalTxt from './avisoLegal.txt?raw';
import LoginAdmin from './LoginAdmin';
import PanelAdmin from './PanelAdmin';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { useEffect, useState } from 'react';

function App() {
  const [adminAutenticado, setAdminAutenticado] = useState(false);
  const [cargandoAuth, setCargandoAuth] = useState(true);

  useEffect(() => {
    const checkSession = async () => {
      const { data: { session } } = await import('./supabaseClient').then(m => m.supabase.auth.getSession());
      setAdminAutenticado(!!session);
      setCargandoAuth(false);
    };
    checkSession();
  }, []);

  return (
    <Router>
      <div className="App">
        <Navbar />
        <Routes>
          <Route path="/admin" element={
            cargandoAuth ? <p>Cargando...</p> :
            adminAutenticado ? <PanelAdmin /> : <LoginAdmin onLogin={() => window.location.reload()} />
          } />
          <Route path="/" element={<IndexArmas />} />
          <Route path="/publicar" element={<FormularioArma />} />
          <Route path="/contacto" element={<div style={{maxWidth:600,margin:'40px auto'}}><h2>Contacto</h2><p>Aquí irá la información de contacto y soporte.</p></div>} />
          <Route path="/aviso-legal" element={
            <div style={{maxWidth:900,margin:'40px auto'}}>
              <h2>Aviso Legal</h2>
              <div style={{background:'#f8f8f8',padding:18,borderRadius:8,marginBottom:24,maxHeight:500,overflowY:'auto',fontSize:14}}>
                <pre style={{whiteSpace:'pre-wrap',fontFamily:'inherit'}}>{avisoLegalTxt}</pre>
              </div>
            </div>
          } />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
