import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { supabase } from './supabaseClient';
import Navbar from './Navbar';

const styles = {
  page: { 
    width: '100vw', 
    minHeight: '100vh', 
    background: '#f5f5f5', 
    color: '#333', 
    boxSizing: 'border-box', 
    padding: 0, 
    margin: 0, 
    overflowX: 'hidden' 
  },
  container: {
    maxWidth: 1200,
    margin: '0 auto',
    padding: '100px 20px 40px',
    background: '#fff'
  },
  breadcrumb: {
    fontSize: 14,
    color: '#666',
    marginBottom: 20,
    display: 'flex',
    alignItems: 'center',
    gap: 8
  },
  breadcrumbLink: {
    color: '#0066cc',
    textDecoration: 'none',
    cursor: 'pointer'
  },
  mainContent: {
    display: 'grid',
    gridTemplateColumns: '1fr 1fr',
    gap: 40,
    marginBottom: 40
  },
  imageSection: {
    display: 'flex',
    flexDirection: 'column'
  },
  mainImage: {
    width: '100%',
    height: 400,
    objectFit: 'contain',
    backgroundColor: '#f8f8f8',
    border: '1px solid #ddd',
    borderRadius: 8,
    marginBottom: 16
  },
  thumbnails: {
    display: 'flex',
    gap: 8,
    flexWrap: 'wrap'
  },
  thumbnail: {
    width: 80,
    height: 60,
    objectFit: 'contain',
    backgroundColor: '#f8f8f8',
    border: '2px solid transparent',
    borderRadius: 4,
    cursor: 'pointer',
    transition: 'border-color 0.2s'
  },
  thumbnailActive: {
    borderColor: '#ff6b35'
  },
  productInfo: {
    display: 'flex',
    flexDirection: 'column'
  },
  title: {
    fontSize: 28,
    fontWeight: 700,
    color: '#333',
    marginBottom: 8
  },
  sku: {
    fontSize: 14,
    color: '#666',
    marginBottom: 16
  },
  condition: {
    display: 'inline-block',
    backgroundColor: '#0066cc',
    color: '#fff',
    padding: '4px 8px',
    borderRadius: 4,
    fontSize: 12,
    fontWeight: 600,
    marginBottom: 8
  },
  rating: {
    display: 'flex',
    alignItems: 'center',
    gap: 8,
    marginBottom: 16
  },
  price: {
    fontSize: 32,
    fontWeight: 700,
    color: '#333',
    marginBottom: 8
  },
  inStock: {
    color: '#28a745',
    fontSize: 14,
    fontWeight: 600,
    marginBottom: 20
  },
  actionButtons: {
    display: 'flex',
    gap: 12,
    marginBottom: 20
  },
  addToCartBtn: {
    backgroundColor: '#ff6b35',
    color: '#fff',
    border: 'none',
    padding: '12px 24px',
    borderRadius: 6,
    fontSize: 16,
    fontWeight: 600,
    cursor: 'pointer',
