import React from 'react';
import { Link, useLocation } from 'react-router-dom';

// Estilos globales para fondo y fuente
if (typeof document !== 'undefined') {
  document.body.style.background = '#e6eaf2'; // gris azulado suave
  document.body.style.fontFamily = 'Segoe UI, Roboto, Arial, Helvetica, sans-serif';
  document.body.style.margin = 0;
}

function Navbar() {
  const location = useLocation();
  const links = [
    { to: '/', label: 'INICIO' },
    { to: '/publicar', label: 'PUBLICAR' },
    { to: '/aviso-legal', label: 'LEGAL' },
    { to: '/contacto', label: 'GUÃA' }
  ];

  // Colores militares y dorado
  const verdeMilitar = 'linear-gradient(90deg, #4b5d3a 0%, #6b7b4c 100%)';
  const doradoMilitar = 'linear-gradient(90deg, #bfa14a 0%, #e6c86b 100%)';
  // Usar el color directamente

  // Responsive styles
  const navStyles = {
    background: '#223a5e',
    color:'#fff',
    padding:'0',
    margin:0,
    width:'100vw',
    minWidth:'100vw',
    position:'fixed',
    left:0,
    top:0,
    zIndex:100,
    boxShadow:'0 2px 8px #0002',
    fontFamily: 'Segoe UI, Roboto, Arial, Helvetica, sans-serif'
  };
  const containerStyles = {
    maxWidth:1200,
    margin:'0 auto',
    display:'flex',
    alignItems:'center',
    justifyContent:'space-between',
    height:64,
    padding:'0 12px',
    flexWrap:'wrap'
  };
  const logoStyles = {
    display:'flex',alignItems:'center',gap:10
  };
  const linksStyles = {
    display:'flex',gap:12,flexWrap:'wrap'
  };
  // Media queries
  const mq = `@media (max-width: 700px)`;
  const mqStyles = {
    [mq]: {
      ...navStyles,
      minWidth:'100vw',
    },
    container: {
      [mq]: {
        flexDirection:'column',
        height:'auto',
        alignItems:'flex-start',
        gap:8,
        padding:'0 6px'
      }
    },
    links: {
      [mq]: {
        flexDirection:'column',
        width:'100%',
        gap:6,
        alignItems:'flex-start',
        padding:'6px 0'
      }
    },
    logo: {
      [mq]: {
        fontSize:20,
        gap:4
      }
    }
  };

  return (
    <nav style={navStyles}>
      <div style={{...containerStyles, ...(mqStyles.container[mq] || {})}}>
        <div style={{...logoStyles, ...(mqStyles.logo[mq] || {})}}>
          <span style={{fontWeight:900, fontSize:28, letterSpacing:2, color:'#fff', textShadow:'1px 1px 2px #111', fontFamily:'inherit'}}>VENTADEARMAS</span>
          <span style={{fontSize:22, marginLeft:8}}>ðŸ‡¦ðŸ‡·</span>
        </div>
        <div style={{...linksStyles, ...(mqStyles.links[mq] || {})}}>
          {links.map(link => (
            <Link
              key={link.to}
              to={link.to}
              style={{
                textDecoration: 'none',
                fontWeight: 700,
                fontSize: 16,
                padding: '8px 22px',
                borderRadius: 8,
                background: location.pathname === link.to ? doradoMilitar : verdeMilitar,
                color: location.pathname === link.to ? '#222' : '#fff',
                border: location.pathname === link.to ? '2px solid #bfa14a' : '2px solid transparent',
                transition: 'all 0.2s',
                letterSpacing: 1,
                cursor: 'pointer',
                marginLeft: 0,
                width: '100%',
                boxSizing: 'border-box',
                ...(window.innerWidth <= 700 ? {marginBottom: 4, fontSize: 15, padding: '10px 0'} : {})
              }}
            >
              {link.label}
            </Link>
          ))}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
