import React from 'react';
import { Link, useLocation } from 'react-router-dom';

// Estilos globales para fondo y fuente
if (typeof document !== 'undefined') {
  document.body.style.background = '#f3f4f7';
  document.body.style.fontFamily = 'Segoe UI, Roboto, Arial, Helvetica, sans-serif';
  document.body.style.margin = 0;
}

function Navbar() {
  const location = useLocation();
  const links = [
    { to: '/', label: 'INICIO' },
    { to: '/publicar', label: 'PUBLICAR' },
    { to: '/aviso-legal', label: 'LEGAL' },
    { to: '/contacto', label: 'GU√çA' }
  ];

  // Colores militares y dorado
  const verdeMilitar = 'linear-gradient(90deg, #4b5d3a 0%, #6b7b4c 100%)';
  const doradoMilitar = 'linear-gradient(90deg, #bfa14a 0%, #e6c86b 100%)';
  const azulNavbar = '#223a5e';

  return (
    <nav style={{
      background: azulNavbar,
      color:'#fff',
      padding:'0',
      margin:0,
      width:'100vw',
      minWidth:'100vw',
      position:'fixed',
      left:0,
      top:0,
      zIndex:100,
      boxShadow:'0 2px 8px #0002',
      fontFamily: 'Segoe UI, Roboto, Arial, Helvetica, sans-serif'
    }}>
      <div style={{maxWidth:1200, margin:'0 auto', display:'flex', alignItems:'center', justifyContent:'space-between', height:64}}>
        <div style={{display:'flex',alignItems:'center',gap:10}}>
          <span style={{fontWeight:900, fontSize:28, letterSpacing:2, color:'#fff', textShadow:'1px 1px 2px #111', fontFamily:'inherit'}}>VENTADEARMAS</span>
          <span style={{fontSize:22, marginLeft:8}}>üá¶üá∑</span>
        </div>
        <div style={{display:'flex',gap:12}}>
          {links.map(link => (
            <Link
              key={link.to}
              to={link.to}
              style={{
                textDecoration: 'none',
                fontWeight: 700,
                fontSize: 16,
                padding: '8px 22px',
                borderRadius: 8,
                background: location.pathname === link.to ? doradoMilitar : verdeMilitar,
                color: location.pathname === link.to ? '#222' : '#fff',
                boxShadow: location.pathname === link.to ? '0 0 8px #bfa14a88' : '0 2px 8px #0002',
                border: location.pathname === link.to ? '2px solid #bfa14a' : '2px solid transparent',
                transition: 'all 0.2s',
                letterSpacing: 1,
                outline: 'none',
                cursor: 'pointer',
                marginLeft: 0
              }}
            >
              {link.label}
            </Link>
          ))}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
