import React, { useEffect, useMemo, useState } from 'react';
import legalUrl from './avisoLegal.txt';

function AvisoLegal() {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [srcIdx, setSrcIdx] = useState(0);
  const sources = useMemo(() => {
    const absolute = typeof window !== 'undefined' ? new URL(legalUrl, window.location.origin).toString() : legalUrl;
    return [absolute, '/avisoLegal.txt'];
  }, []);

  useEffect(() => {
    setLoading(true);
    setError('');
  }, [srcIdx]);

  return (
    <div style={{maxWidth:900,margin:'110px auto 40px auto'}}>
      <h2>Aviso Legal</h2>
      <div style={{background:'#f8f8f8',padding:0,borderRadius:8,marginBottom:24,overflow:'hidden',fontSize:14,border:'1px solid #ddd'}}>
        {loading && !error && <div style={{padding:18}}>Cargando...</div>}
        {!error && (
          <iframe
            title="Aviso Legal"
            src={sources[srcIdx]}
            style={{width:'100%',height:500,border:'none',display: loading ? 'none' : 'block', background:'#fff'}}
            onLoad={() => setLoading(false)}
            onError={() => {
              if (srcIdx + 1 < sources.length) setSrcIdx(srcIdx + 1); else { setError('No se pudo cargar el Aviso Legal. Intentalo más tarde.'); setLoading(false); }
            }}
          />
        )}
        {error && <div style={{padding:18,color:'red'}}>{error}</div>}
      </div>
    </div>
  );
}

export default AvisoLegal;
