import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { supabase } from './supabaseClient';
import Navbar from './Navbar';

const styles = {
  page: { 
    width: '100vw', 
    minHeight: '100vh', 
    background: '#f8f9fa', 
    color: '#333', 
    boxSizing: 'border-box', 
    padding: 0, 
    margin: 0, 
    overflowX: 'hidden' 
  },
  container: {
    maxWidth: 1200,
    margin: '0 auto',
    padding: '100px 20px 40px',
    background: '#fff',
    minHeight: '100vh'
  },
  backBtn: {
    position: 'absolute',
    top: 80,
    left: 20,
    backgroundColor: '#223a5e',
    color: '#fff',
    border: 'none',
    borderRadius: 6,
    padding: '8px 16px',
    cursor: 'pointer',
    fontSize: 14,
    zIndex: 10
  },
  mainContent: {
    display: 'grid',
    gridTemplateColumns: '1.2fr 1fr',
    gap: 40,
    marginBottom: 40
  },
  mobileContent: {
    display: 'flex',
    flexDirection: 'column',
    gap: 20
  },
  imageSection: {
    display: 'flex',
    flexDirection: 'column'
  },
  mainImage: {
    width: '100%',
    height: 500,
    objectFit: 'contain',
    backgroundColor: '#fff',
    border: '2px solid #ffc107',
    borderRadius: 12,
    marginBottom: 16,
    cursor: 'zoom-in'
  },
  mobileImage: {
    width: '100%',
    height: 300,
    objectFit: 'contain',
    backgroundColor: '#fff',
    border: '2px solid #ffc107',
    borderRadius: 12,
    marginBottom: 16,
    cursor: 'zoom-in'
  },
  thumbnails: {
    display: 'flex',
    gap: 8,
    flexWrap: 'wrap',
    justifyContent: 'center'
  },
  thumbnail: {
    width: 80,
    height: 60,
    objectFit: 'contain',
    backgroundColor: '#fff',
    border: '2px solid #ddd',
    borderRadius: 6,
    cursor: 'pointer',
    transition: 'border-color 0.2s'
  },
  thumbnailActive: {
    borderColor: '#ffc107'
  },
  infoSection: {
    display: 'flex',
    flexDirection: 'column',
    gap: 20
  },
  title: {
    fontSize: 32,
    fontWeight: 700,
    color: '#ffc107',
    marginBottom: 16,
    textShadow: '1px 1px 2px rgba(0,0,0,0.1)'
  },
  mobileTitle: {
    fontSize: 24,
    fontWeight: 700,
    color: '#ffc107',
    marginBottom: 16,
    textAlign: 'center',
    textShadow: '1px 1px 2px rgba(0,0,0,0.1)'
  },
  price: {
    fontSize: 28,
    fontWeight: 700,
    color: '#223a5e',
    marginBottom: 16
  },
  statusBadge: {
    display: 'inline-block',
    padding: '6px 12px',
    borderRadius: 20,
    fontSize: 14,
    fontWeight: 600,
    marginBottom: 20
  },
  statusNew: {
    backgroundColor: '#28a745',
    color: '#fff'
  },
  statusUsed: {
    backgroundColor: '#ffc107',
    color: '#000'
  },
  statusSold: {
    backgroundColor: '#dc3545',
    color: '#fff'
  },
  specs: {
    display: 'grid',
    gridTemplateColumns: '1fr 1fr',
    gap: 12,
    marginBottom: 20
  },
  mobileSpecs: {
    display: 'flex',
    flexDirection: 'column',
    gap: 8,
    marginBottom: 20
  },
  specItem: {
    display: 'flex',
    padding: '8px 12px',
    backgroundColor: '#f8f9fa',
    borderRadius: 6,
    border: '1px solid #e9ecef'
  },
  specLabel: {
    fontWeight: 600,
    marginRight: 8,
    color: '#495057',
    minWidth: 80
  },
  specValue: {
    color: '#6c757d'
  },
  contactCard: {
    background: 'linear-gradient(135deg, #223a5e 0%, #2c5282 100%)',
    color: '#fff',
    padding: 24,
    borderRadius: 12,
    marginTop: 20,
    boxShadow: '0 4px 12px rgba(34, 58, 94, 0.3)'
  },
  contactTitle: {
    fontSize: 20,
    fontWeight: 700,
    marginBottom: 16,
    color: '#ffc107',
    textAlign: 'center'
  },
  contactInfo: {
    fontSize: 16,
    lineHeight: 1.8,
    textAlign: 'center'
  },
  contactButtons: {
    display: 'flex',
    gap: 12,
    marginTop: 16,
    justifyContent: 'center'
  },
  callBtn: {
    backgroundColor: '#28a745',
    color: '#fff',
    border: 'none',
    padding: '12px 20px',
    borderRadius: 8,
    fontSize: 16,
    fontWeight: 600,
    cursor: 'pointer',
    textDecoration: 'none',
    display: 'flex',
    alignItems: 'center',
    gap: 8
  },
  emailBtn: {
    backgroundColor: '#ffc107',
    color: '#000',
    border: 'none',
    padding: '12px 20px',
    borderRadius: 8,
    fontSize: 16,
    fontWeight: 600,
    cursor: 'pointer',
    textDecoration: 'none',
    display: 'flex',
    alignItems: 'center',
    gap: 8
  },
  description: {
    backgroundColor: '#f8f9fa',
    padding: 20,
    borderRadius: 8,
    marginTop: 20,
    border: '1px solid #e9ecef'
  },
  descriptionTitle: {
    fontSize: 18,
    fontWeight: 600,
    marginBottom: 12,
    color: '#495057'
  },
  descriptionText: {
    lineHeight: 1.6,
    color: '#6c757d'
  },
  noContactCard: {
    background: 'linear-gradient(135deg, #ffc107 0%, #ffb300 100%)',
    color: '#000',
    padding: 24,
    borderRadius: 12,
    marginTop: 20,
    textAlign: 'center',
    fontWeight: 600,
    fontSize: 16
  }
};



function DetalleArma() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [arma, setArma] = useState(null);
  const [loading, setLoading] = useState(true);
  const [fotoIdx, setFotoIdx] = useState(0);
  const [modalOpen, setModalOpen] = useState(false);

  useEffect(() => {
    const fetchArma = async () => {
      const { data } = await supabase.from('armas').select('*').eq('id', id).single();
      setArma(data);
      setLoading(false);
    };
    fetchArma();
  }, [id]);

  if (loading) return (
    <div>
      <Navbar />
      <div style={{textAlign:'center', marginTop: 150, color: '#666'}}>Cargando...</div>
    </div>
  );

  if (!arma) return (
    <div>
      <Navbar />
      <div style={{textAlign:'center', marginTop: 150, color: '#dc3545'}}>No se encontr√≥ el arma.</div>
    </div>
  );

  const fotos = arma.fotos || [];
  const isMobile = window.innerWidth <= 768;

  const getStatusBadge = () => {
    if (arma.estado_publicacion === 'VENDIDA') {
      return <span style={{...styles.statusBadge, ...styles.statusSold}}>VENDIDO</span>;
    }
    return (
      <span style={{
        ...styles.statusBadge, 
        ...(arma.estado_arma === 'Nueva' ? styles.statusNew : styles.statusUsed)
      }}>
        {arma.estado_arma === 'Nueva' ? 'NUEVO' : 'USADO'}
      </span>
    );
  };

  return (
    <>
      <Navbar />
      <div style={styles.page}>
        <button style={styles.backBtn} onClick={() => navigate(-1)}>
          ‚Üê Volver
        </button>

        <div style={styles.container}>
          <div style={isMobile ? styles.mobileContent : styles.mainContent}>

            {/* Secci√≥n de im√°genes */}
            <div style={styles.imageSection}>
              {/* T√≠tulo en m√≥vil */}
              {isMobile && (
                <h1 style={styles.mobileTitle}>
                  {arma.marca} {arma.modelo}
                </h1>
              )}

              {fotos.length > 0 && (
                <>
                  <img
                    src={fotos[fotoIdx]}
                    alt={`${arma.marca} ${arma.modelo}`}
                    style={isMobile ? styles.mobileImage : styles.mainImage}
                    onClick={() => setModalOpen(true)}
                    title="Click para ampliar"
                  />
                  {fotos.length > 1 && (
                    <div style={styles.thumbnails}>
                      {fotos.map((foto, idx) => (
                        <img
                          key={idx}
                          src={foto}
                          alt={`Vista ${idx + 1}`}
                          style={{
                            ...styles.thumbnail,
                            ...(idx === fotoIdx ? styles.thumbnailActive : {})
                          }}
                          onClick={() => setFotoIdx(idx)}
                        />
                      ))}
                    </div>
                  )}
                </>
              )}
            </div>

            {/* Informaci√≥n del arma */}
            <div style={styles.infoSection}>
              {/* T√≠tulo en desktop */}
              {!isMobile && (
                <h1 style={styles.title}>
                  {arma.marca} {arma.modelo}
                </h1>
              )}

              {/* Estado y precio */}
              {getStatusBadge()}

              <div style={styles.price}>
                {arma.moneda === 'USD' ? 'US$' : '$'}{arma.precio_venta}
                {arma.moneda === 'USD' ? ' D√≥lares' : ' Pesos AR'}
              </div>

              {/* Especificaciones */}
              <div style={isMobile ? styles.mobileSpecs : styles.specs}>
                <div style={styles.specItem}>
                  <span style={styles.specLabel}>Tipo:</span>
                  <span style={styles.specValue}>{arma.tipo_arma}</span>
                </div>
                <div style={styles.specItem}>
                  <span style={styles.specLabel}>Calibre:</span>
                  <span style={styles.specValue}>{arma.calibre}</span>
                </div>
                <div style={styles.specItem}>
                  <span style={styles.specLabel}>Marca:</span>
                  <span style={styles.specValue}>{arma.marca}</span>
                </div>
                <div style={styles.specItem}>
                  <span style={styles.specLabel}>Modelo:</span>
                  <span style={styles.specValue}>{arma.modelo}</span>
                </div>
                <div style={styles.specItem}>
                  <span style={styles.specLabel}>Ubicaci√≥n:</span>
                  <span style={styles.specValue}>{arma.ciudad}, {arma.provincia}</span>
                </div>
                <div style={styles.specItem}>
                  <span style={styles.specLabel}>Empadronamiento:</span>
                  <span style={styles.specValue}>{arma.empadronamiento}</span>
                </div>
              </div>

              {/* Tarjeta de contacto - PROTAGONISTA */}
              {arma.estado_publicacion === 'PUBLICADA_CON_CONTACTO' && (
                <div style={styles.contactCard}>
                  <div style={styles.contactTitle}>
                    üìû CONTACTAR AL VENDEDOR
                  </div>
                  <div style={styles.contactInfo}>
                    <strong>{arma.nombre}</strong><br/>
                    üìç {arma.ciudad}, {arma.provincia}<br/>
                    üìû {arma.telefono}<br/>
                    ‚úâÔ∏è {arma.email}
                  </div>
                  <div style={styles.contactButtons}>
                    <a href={`tel:${arma.telefono}`} style={styles.callBtn}>
                      üìû Llamar
                    </a>
                    <a href={`mailto:${arma.email}?subject=${arma.marca} ${arma.modelo}`} style={styles.emailBtn}>
                      ‚úâÔ∏è Email
                    </a>
                  </div>
                </div>
              )}

              {arma.estado_publicacion === 'VENDIDA' && (
                <div style={styles.contactCard}>
                  <div style={{...styles.contactTitle, color: '#fff'}}>
                    üö´ ARMA VENDIDA
                  </div>
                  <div style={{...styles.contactInfo, textAlign: 'center', fontSize: 18}}>
                    Esta publicaci√≥n ya no est√° disponible
                  </div>
                </div>
              )}

              {arma.estado_publicacion === 'PUBLICADA_SIN_CONTACTO' && (
                <div style={styles.noContactCard}>
                  üîí Informaci√≥n de contacto disponible tras el pago
                </div>
              )}

              {/* Descripci√≥n/comentarios */}
              {arma.comentarios && (
                <div style={styles.description}>
                  <div style={styles.descriptionTitle}>
                    Descripci√≥n del vendedor:
                  </div>
                  <div style={styles.descriptionText}>
                    {arma.comentarios}
                  </div>
                </div>
              )}

              {/* Informaci√≥n adicional */}
              <div style={styles.description}>
                <div style={styles.descriptionTitle}>
                  Informaci√≥n adicional:
                </div>
                <div style={styles.descriptionText}>
                  <strong>N√∫mero de serie:</strong> {arma.numero_serie}<br/>
                  <strong>Pa√≠s:</strong> {arma.pais}
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Modal para imagen ampliada */}
        {modalOpen && fotos.length > 0 && (
          <div 
            style={{
              position: 'fixed',
              top: 0,
              left: 0,
              right: 0,
              bottom: 0,
              backgroundColor: 'rgba(0,0,0,0.9)',
              zIndex: 1000,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              padding: 20
            }}
            onClick={() => setModalOpen(false)}
          >
            <div style={{position: 'relative', maxWidth: '90vw', maxHeight: '90vh'}}>
              <img
                src={fotos[fotoIdx]}
                alt={`${arma.marca} ${arma.modelo}`}
                style={{
                  maxWidth: '100%',
                  maxHeight: '100%',
                  objectFit: 'contain',
                  borderRadius: 8
                }}
                onClick={(e) => e.stopPropagation()}
              />
              <button
                style={{
                  position: 'absolute',
                  top: -40,
                  right: 0,
                  background: '#fff',
                  border: 'none',
                  borderRadius: '50%',
                  width: 32,
                  height: 32,
                  cursor: 'pointer',
                  fontSize: 18,
                  fontWeight: 'bold'
                }}
                onClick={() => setModalOpen(false)}
              >
                √ó
              </button>
            </div>
          </div>
        )}
      </div>
    </>
  );
}

export default DetalleArma;
