import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { supabase } from './supabaseClient';
import Navbar from './Navbar';

const styles = {
  page: { width: '100vw', minHeight: '100vh', background: 'transparent', color: '#223a5e', boxSizing: 'border-box', padding: 0, margin: 0 },
  btnVolver: { position: 'absolute', top: 32, right: 48, background: '#1976d2', color: '#fff', border: 'none', borderRadius: 6, padding: '10px 28px', fontWeight: 600, cursor: 'pointer', fontSize: 18, zIndex: 10 },
  main: { display: 'flex', flexDirection: 'row', alignItems: 'flex-start', justifyContent: 'center', width: '100vw', maxWidth: '100vw', marginTop: 80, gap: 48, boxSizing: 'border-box', padding: '0 32px' },
  imgBox: { flex: '0 0 520px', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', minHeight: 400 },
  img: { width: 480, height: 360, objectFit: 'contain', border: '2px solid #ffc107', borderRadius: 12, background: '#fff', marginBottom: 16 },
  galeriaBtns: { display: 'flex', gap: 12, marginTop: 8 },
  galeriaBtn: { background: '#223a5e', color: '#fff', border: 'none', borderRadius: 6, padding: '6px 18px', fontWeight: 600, cursor: 'pointer', fontSize: 15 },
  infoBox: { flex: 1, minWidth: 320, maxWidth: 600, display: 'flex', flexDirection: 'column', gap: 18, alignItems: 'flex-start', marginTop: 24 },
  titulo: { color: '#ffc107', fontWeight: 700, fontSize: 38, marginBottom: 8, letterSpacing: 1, textShadow: '1px 1px 2px #222', textAlign: 'left' },
  info: { fontSize: 20, marginBottom: 0, textAlign: 'left', color: '#223a5e' },
  contacto: { background: '#fff2', borderRadius: 10, padding: 16, marginTop: 18, color: '#223a5e', fontSize: 18, width: '100%' }
};


function DetalleArma() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [arma, setArma] = useState(null);
  const [loading, setLoading] = useState(true);
  const [fotoIdx, setFotoIdx] = useState(0);

  useEffect(() => {
    const fetchArma = async () => {
      const { data } = await supabase.from('armas').select('*').eq('id', id).single();
      setArma(data);
      setLoading(false);
    };
    fetchArma();
  }, [id]);

  if (loading) return <div style={{color:'#223a5e',textAlign:'center',marginTop:80}}>Cargando...</div>;
  if (!arma) return <div style={{color:'red',textAlign:'center',marginTop:80}}>No se encontrÃ³ el arma.</div>;

  const fotos = arma.fotos || [];
  const mostrarAnterior = () => setFotoIdx(idx => (idx > 0 ? idx - 1 : fotos.length - 1));
  const mostrarSiguiente = () => setFotoIdx(idx => (idx < fotos.length - 1 ? idx + 1 : 0));

  return (
    <>
      <Navbar />
      <div style={styles.page}>
        <button style={styles.btnVolver} onClick={() => navigate(-1)}>Volver</button>
        <div style={styles.main}>
          <div style={styles.imgBox}>
            {fotos.length > 0 && (
              <img src={fotos[fotoIdx]} alt={`foto${fotoIdx+1}`} style={styles.img} />
            )}
            {fotos.length > 1 && (
              <div style={styles.galeriaBtns}>
                <button style={styles.galeriaBtn} onClick={mostrarAnterior}>&lt;</button>
                <span style={{color:'#ffc107', fontWeight:700, fontSize:18}}>{fotoIdx+1} / {fotos.length}</span>
                <button style={styles.galeriaBtn} onClick={mostrarSiguiente}>&gt;</button>
              </div>
            )}
          </div>
          <div style={styles.infoBox}>
            <div style={styles.titulo}>{arma.marca} {arma.modelo}</div>
            <div style={styles.info}><b>Tipo:</b> {arma.tipo_arma}</div>
            <div style={styles.info}><b>Calibre:</b> {arma.calibre}</div>
            <div style={styles.info}><b>Estado:</b> {arma.estado_arma}</div>
            <div style={styles.info}><b>Precio:</b> {arma.moneda === 'USD' ? 'US$' : '$'} {arma.precio_venta} {arma.moneda === 'USD' ? 'DÃ³lares' : 'Pesos AR'}</div>
            <div style={styles.info}><b>UbicaciÃ³n:</b> {arma.ciudad}, {arma.provincia}, {arma.pais}</div>
            <div style={styles.info}><b>Comentarios:</b> {arma.comentarios}</div>
            <div style={styles.info}><b>Empadronamiento:</b> {arma.empadronamiento}</div>
            <div style={styles.info}><b>NÂ° Serie:</b> {arma.numero_serie}</div>
            <div style={styles.contacto}>
              <b>Contacto</b><br/>
              {arma.nombre}<br/>
              {arma.telefono}<br/>
              {arma.email}<br/>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default DetalleArma;
