import React, { useState } from 'react';
import { supabase } from './supabaseClient';

const camposIniciales = {
	nombre: '',
	pais: '',
	ciudad: '',
	provincia: '',
	telefono: '',
	email: '',
	precio_venta: '',
	moneda: 'USD',
	marca: '',
	modelo: '',
	numero_serie: '',
	tipo_arma: '',
	calibre: '',
	estado_arma: '',
	empadronamiento: '',
	comentarios: '',
	fotos: []
};

const opcionesTipoArma = [
	'Pistola','Revolver', 'Fusil', 'Carabina', 'Escopeta', 'Mira telescopica', 'Otro'
];
const opcionesEstadoArma = ['Nueva', 'Usada'];
const opcionesEmpadronamiento = [
	'Reempadronada', 'En tramite ANMAC', 'Tenencia Vigente', 'Tenencia Vencida', 'En venta NUEVA'
];

function FormularioArma() {
	const [campos, setCampos] = useState(camposIniciales);
	const [fotosPreview, setFotosPreview] = useState([]);
	const [mensaje, setMensaje] = useState('');
	const [cargando, setCargando] = useState(false);

	const handleChange = (e) => {
		const { name, value } = e.target;
		setCampos({ ...campos, [name]: value });
	};

	const handleFotos = (e) => {
		const files = Array.from(e.target.files).slice(0, 10);
		setCampos({ ...campos, fotos: files });
		setFotosPreview(files.map(file => URL.createObjectURL(file)));
	};


	const handleSubmit = async (e) => {
		e.preventDefault();
		setCargando(true);
		setMensaje('');

		// Subir fotos a Supabase Storage y obtener URLs públicas
		let fotosUrls = [];
		if (campos.fotos && campos.fotos.length > 0) {
			for (let i = 0; i < campos.fotos.length; i++) {
				const foto = campos.fotos[i];
				const nombreArchivo = `${Date.now()}_${Math.random().toString(36).substring(2, 8)}_${foto.name}`;
				const { data: uploadData, error: uploadError } = await supabase.storage
					.from('armas-fotos')
					.upload(nombreArchivo, foto, { cacheControl: '3600', upsert: false });
				if (uploadError) {
					setMensaje('Error al subir la foto: ' + uploadError.message);
					setCargando(false);
					return;
				}
				// Obtener URL pública temporal
				const { data: urlData } = supabase.storage.from('armas-fotos').getPublicUrl(nombreArchivo);
				fotosUrls.push(urlData.publicUrl);
			}
		}

		const { fotos, ...datosArma } = campos;
		const { data, error } = await supabase
			.from('armas')
			.insert([{ ...datosArma, fotos: fotosUrls }]);

		if (error) {
			setMensaje('Error al enviar: ' + error.message);
		} else {
			setMensaje('¡Arma enviada correctamente!');
			setCampos(camposIniciales);
			setFotosPreview([]);
		}
		setCargando(false);
	};

	return (
		<form onSubmit={handleSubmit}>
			<h2>Cargar un arma</h2>
			<input name="nombre" placeholder="Nombre" value={campos.nombre} onChange={handleChange} required />
			<input name="pais" placeholder="País" value={campos.pais} onChange={handleChange} required />
			<input name="ciudad" placeholder="Ciudad" value={campos.ciudad} onChange={handleChange} required />
			<input name="provincia" placeholder="Provincia" value={campos.provincia} onChange={handleChange} required />
			<input name="telefono" placeholder="Teléfono" value={campos.telefono} onChange={handleChange} required />
			<input name="email" type="email" placeholder="Email" value={campos.email} onChange={handleChange} required />
			<input name="precio_venta" type="number" placeholder="Precio de venta" value={campos.precio_venta} onChange={handleChange} required />
			<select name="moneda" value={campos.moneda} onChange={handleChange} required>
				<option value="USD">USD</option>
				<option value="ARS">Pesos Argentinos</option>
			</select>
			<input name="marca" placeholder="Marca del arma" value={campos.marca} onChange={handleChange} required />
			<input name="modelo" placeholder="Modelo del arma" value={campos.modelo} onChange={handleChange} required />
			<input name="numero_serie" placeholder="Número de serie" value={campos.numero_serie} onChange={handleChange} required />
			<select name="tipo_arma" value={campos.tipo_arma} onChange={handleChange} required>
				<option value="">Tipo de arma</option>
				{opcionesTipoArma.map(op => <option key={op} value={op}>{op}</option>)}
			</select>
			<input name="calibre" placeholder="Calibre del arma" value={campos.calibre} onChange={handleChange} required />
			<select name="estado_arma" value={campos.estado_arma} onChange={handleChange} required>
				<option value="">Estado del arma</option>
				{opcionesEstadoArma.map(op => <option key={op} value={op}>{op}</option>)}
			</select>
			<select name="empadronamiento" value={campos.empadronamiento} onChange={handleChange} required>
				<option value="">Empadronamiento / Registro</option>
				{opcionesEmpadronamiento.map(op => <option key={op} value={op}>{op}</option>)}
			</select>
			<textarea name="comentarios" placeholder="Comentarios y detalles" value={campos.comentarios} onChange={handleChange} />
			<input name="fotos" type="file" accept="image/*" multiple onChange={handleFotos} />
			<div style={{ display: 'flex', gap: 8, flexWrap: 'wrap' }}>
				{fotosPreview.map((src, i) => (
					<img key={i} src={src} alt="preview" width={80} />
				))}
			</div>
			<button type="submit" disabled={cargando}>{cargando ? 'Enviando...' : 'Enviar'}</button>
			{mensaje && <p>{mensaje}</p>}
		</form>
	);
}

export default FormularioArma;
