import React, { useEffect, useState } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { supabase } from './supabaseClient';
import './Navbar.css';

// Estilos globales para fondo y fuente
if (typeof document !== 'undefined') {
  document.body.style.background = '#e6e0d8'; // neutral warm gray
  document.body.style.fontFamily = 'Segoe UI, Roboto, Arial, Helvetica, sans-serif';
  document.body.style.margin = 0;
}

function Navbar() {
  const location = useLocation();
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [isMobile, setIsMobile] = useState(typeof window !== 'undefined' ? window.innerWidth <= 700 : false);
  const links = [
    { to: '/', label: 'INICIO' },
    { to: '/publicar', label: 'PUBLICAR' },
    { to: '/aviso-legal', label: 'LEGAL' },
    { to: '/contacto', label: 'GUÃA' }
  ];

  useEffect(() => {
    supabase.auth.getSession().then(({ data: { session } }) => {
      setUser(session?.user || null);
    });
    const { data: listener } = supabase.auth.onAuthStateChange((_event, session) => {
      setUser(session?.user || null);
    });
    const onResize = () => setIsMobile(typeof window !== 'undefined' ? window.innerWidth <= 700 : false);
    window.addEventListener('resize', onResize);
    return () => { listener?.subscription?.unsubscribe(); };
  }, []);

  return (
    <nav className="navbar-root">
      <div className="navbar-container">
        <div className="navbar-title">
          <span>VENTADEARMAS</span>
          <span className="navbar-flags">ðŸ‡¦ðŸ‡·</span>
        </div>
        <div className="navbar-links">
          {links.map(link => (
            <Link
              key={link.to}
              to={link.to}
              className={
                'navbar-link' + (location.pathname === link.to ? ' active' : '')
              }
            >
              {link.label}
            </Link>
          ))}
          {!user ? (
            <Link
              to="/login"
              className="navbar-link"
              style={{
                background: 'linear-gradient(90deg, #bfa14a 0%, #e6c86b 100%)',
                color: '#222',
                border: '2px solid #bfa14a',
                fontWeight: 900,
                textDecoration: 'none',
                borderRadius: 8,
                letterSpacing: 1
              }}
            >
              {isMobile ? 'ENTRAR' : 'INICIAR SESIÃ“N'}
            </Link>
          ) : (
            <button
              onClick={() => navigate('/perfil')}
              className="navbar-link"
              style={{
                background: 'linear-gradient(90deg, #bfa14a 0%, #e6c86b 100%)',
                color: '#222',
                border: '2px solid #bfa14a',
                fontWeight: 900,
                borderRadius: 8,
                letterSpacing: 1,
                cursor: 'pointer'
              }}
            >
              PERFIL
            </button>
          )}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
