import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Navbar.css';

// Estilos globales para fondo y fuente
if (typeof document !== 'undefined') {
  document.body.style.background = '#e6eaf2'; // gris azulado suave
  document.body.style.fontFamily = 'Segoe UI, Roboto, Arial, Helvetica, sans-serif';
  document.body.style.margin = 0;
}

function Navbar() {
  const location = useLocation();
  const links = [
    { to: '/', label: 'INICIO' },
    { to: '/publicar', label: 'PUBLICAR' },
    { to: '/aviso-legal', label: 'LEGAL' },
    { to: '/contacto', label: 'GUÍA' }
  ];

  // Simulación de sesión (luego se reemplaza por lógica real)
  const isLogged = false;

  return (
    <nav className="navbar-root">
      <div className="navbar-container">
        <div className="navbar-title">
          <span>VENTADEARMAS</span>
          <span className="navbar-flags">🇦🇷</span>
        </div>
        <div className="navbar-links">
          {links.map(link => (
            <Link
              key={link.to}
              to={link.to}
              className={
                'navbar-link' + (location.pathname === link.to ? ' active' : '')
              }
            >
              {link.label}
            </Link>
          ))}
          {!isLogged ? (
            <Link
              to="/login"
              style={{
                background: 'linear-gradient(90deg, #bfa14a 0%, #e6c86b 100%)',
                color: '#222',
                border: '2px solid #bfa14a',
                fontWeight: 900,
                marginLeft: 16,
                borderRadius: 8,
                padding: '8px 22px',
                textDecoration: 'none',
                letterSpacing: 1,
                fontSize: 16
              }}
            >
              INICIAR SESIÓN
            </Link>
          ) : (
            <Link
              to="/perfil"
              style={{
                background: 'linear-gradient(90deg, #4b5d3a 0%, #6b7b4c 100%)',
                color: '#fff',
                border: '2px solid #bfa14a',
                fontWeight: 900,
                marginLeft: 16,
                borderRadius: 8,
                padding: '8px 22px',
                textDecoration: 'none',
                letterSpacing: 1,
                fontSize: 16
              }}
            >
              PERFIL
            </Link>
          )}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
