import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { supabase } from './supabaseClient';
import Navbar from './Navbar';

const styles = {
  container: { maxWidth: 900, margin: '0 auto', padding: 32, background: '#223a5e', borderRadius: 18, color: '#fff', marginTop: 48, boxShadow: '0 2px 16px #0002' },
  titulo: { color: '#ffc107', fontWeight: 700, fontSize: 32, marginBottom: 18, letterSpacing: 1, textShadow: '1px 1px 2px #222', textAlign: 'left' },
  galeria: { display: 'flex', gap: 16, flexWrap: 'wrap', marginBottom: 24 },
  img: { width: 220, height: 180, objectFit: 'contain', border: '2px solid #ffc107', borderRadius: 8, background: '#fff' },
  info: { fontSize: 18, marginBottom: 10, textAlign: 'left' },
  contacto: { background: '#fff2', borderRadius: 10, padding: 16, marginTop: 24, color: '#fff', fontSize: 17 },
  btnVolver: { background: '#1976d2', color: '#fff', border: 'none', borderRadius: 6, padding: '8px 22px', fontWeight: 600, cursor: 'pointer', fontSize: 16, marginBottom: 24 }
};

function DetalleArma() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [arma, setArma] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchArma = async () => {
      const { data, error } = await supabase.from('armas').select('*').eq('id', id).single();
      setArma(data);
      setLoading(false);
    };
    fetchArma();
  }, [id]);

  if (loading) return <div style={{color:'#223a5e',textAlign:'center',marginTop:80}}>Cargando...</div>;
  if (!arma) return <div style={{color:'red',textAlign:'center',marginTop:80}}>No se encontró el arma.</div>;

  return (
    <>
      <Navbar />
      <div style={styles.container}>
        <button style={styles.btnVolver} onClick={() => navigate(-1)}>Volver</button>
        <div style={styles.titulo}>{arma.marca} {arma.modelo}</div>
        <div style={styles.galeria}>
          {arma.fotos && arma.fotos.map((url, i) => (
            <img key={i} src={url} alt={`foto${i+1}`} style={styles.img} />
          ))}
        </div>
        <div style={styles.info}><b>Tipo:</b> {arma.tipo_arma}</div>
        <div style={styles.info}><b>Calibre:</b> {arma.calibre}</div>
        <div style={styles.info}><b>Estado:</b> {arma.estado_arma}</div>
        <div style={styles.info}><b>Precio:</b> {arma.moneda === 'USD' ? 'US$' : '$'} {arma.precio_venta} {arma.moneda === 'USD' ? 'Dólares' : 'Pesos AR'}</div>
        <div style={styles.info}><b>Ubicación:</b> {arma.ciudad}, {arma.provincia}, {arma.pais}</div>
        <div style={styles.info}><b>Comentarios:</b> {arma.comentarios}</div>
        <div style={styles.info}><b>Empadronamiento:</b> {arma.empadronamiento}</div>
        <div style={styles.info}><b>N° Serie:</b> {arma.numero_serie}</div>
        <div style={styles.contacto}>
          <b>Contacto</b><br/>
          {arma.nombre}<br/>
          {arma.telefono}<br/>
          {arma.email}<br/>
        </div>
      </div>
    </>
  );
}

export default DetalleArma;
