import React, { useState } from 'react';
import { supabase } from './supabaseClient';

function LoginRegistro({ onAuth }) {
  const [isLogin, setIsLogin] = useState(true);
  // Campos para registro
  const [nombreCompleto, setNombreCompleto] = useState('');
  const [telefono, setTelefono] = useState('');
  const [email, setEmail] = useState('');
  const [email2, setEmail2] = useState('');
  const [password, setPassword] = useState('');
  const [password2, setPassword2] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [mensaje, setMensaje] = useState('');

  const handleForgotPassword = async () => {
    const emailPrompt = window.prompt('Ingresa tu email para recuperar la contraseña:');
    if (!emailPrompt) return;
    const { error } = await supabase.auth.resetPasswordForEmail(emailPrompt, { redirectTo: window.location.origin + '/login' });
    if (error) setMensaje(error.message);
    else setMensaje('Te enviamos un email para recuperar tu contraseña.');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setMensaje('');
    if (isLogin) {
      const { error } = await supabase.auth.signInWithPassword({ email, password });
      if (error) setError(error.message);
      else onAuth && onAuth();
    } else {
      // Validaciones
      if (!nombreCompleto) {
        setError('Debes ingresar tu nombre completo.'); setLoading(false); return;
      }
      if (!telefono) {
        setError('Debes ingresar tu número de teléfono.'); setLoading(false); return;
      }
      if (email !== email2) {
        setError('Los emails no coinciden.'); setLoading(false); return;
      }
      if (password !== password2) {
        setError('Las contraseñas no coinciden.'); setLoading(false); return;
      }
      const { error } = await supabase.auth.signUp({
        email,
        password,
        options: {
          data: {
            nombre_completo: nombreCompleto,
            telefono: telefono
          }
        }
      });
      if (error) setError(error.message);
      else setMensaje('Revisa tu email para confirmar el registro.');
    }
    setLoading(false);
  };

  return (
    <div style={{ minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', background: '#fff' }}>
  <form onSubmit={handleSubmit} style={{ background: '#fff', border: '1px solid #dee2e6', borderRadius: 12, boxShadow: '0 4px 16px rgba(0,0,0,0.1)', padding: 40, minWidth: 320, maxWidth: 400, color: '#333', display: 'flex', flexDirection: 'column', gap: 20, alignItems: 'center' }}>
        <div style={{ color: '#223a5e', fontWeight: 600, fontSize: 28, textAlign: 'center', margin: '0 0 20px 0', fontFamily: '"Roboto Condensed", sans-serif' }}>
          {isLogin ? 'Iniciar Sesión' : 'Registrarse'}
        </div>
        {!isLogin && (
          <>
            <input type="text" placeholder="Nombre completo" value={nombreCompleto} onChange={e => setNombreCompleto(e.target.value)} required style={{ border: '1px solid #dee2e6', borderRadius: 8, padding: '12px 16px', fontSize: 16, width: '100%', background: '#f8f9fa', color: '#333', fontWeight: 400, boxSizing: 'border-box' }} />
            <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} required style={{ border: '1px solid #dee2e6', borderRadius: 8, padding: '12px 16px', fontSize: 16, width: '100%', background: '#f8f9fa', color: '#333', fontWeight: 400, boxSizing: 'border-box' }} />
            <input type="email" placeholder="Repetir email" value={email2} onChange={e => setEmail2(e.target.value)} required style={{ border: '1px solid #dee2e6', borderRadius: 8, padding: '12px 16px', fontSize: 16, width: '100%', background: '#f8f9fa', color: '#333', fontWeight: 400, boxSizing: 'border-box' }} />
            <input type="text" placeholder="Teléfono" value={telefono} onChange={e => setTelefono(e.target.value)} required style={{ border: '1px solid #dee2e6', borderRadius: 8, padding: '12px 16px', fontSize: 16, width: '100%', background: '#f8f9fa', color: '#333', fontWeight: 400, boxSizing: 'border-box' }} />
            <input type="password" placeholder="Contraseña" value={password} onChange={e => setPassword(e.target.value)} required style={{ border: '1px solid #dee2e6', borderRadius: 8, padding: '12px 16px', fontSize: 16, width: '100%', background: '#f8f9fa', color: '#333', fontWeight: 400, boxSizing: 'border-box' }} />
            <input type="password" placeholder="Repetir contraseña" value={password2} onChange={e => setPassword2(e.target.value)} required style={{ border: '1px solid #dee2e6', borderRadius: 8, padding: '12px 16px', fontSize: 16, width: '100%', background: '#f8f9fa', color: '#333', fontWeight: 400, boxSizing: 'border-box' }} />
          </>
        )}
        {isLogin && (
          <>
            <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} required style={{ border: '1px solid #dee2e6', borderRadius: 8, padding: '12px 16px', fontSize: 16, width: '100%', background: '#f8f9fa', color: '#333', fontWeight: 400, boxSizing: 'border-box' }} />
            <input type="password" placeholder="Contraseña" value={password} onChange={e => setPassword(e.target.value)} required style={{ border: '1px solid #dee2e6', borderRadius: 8, padding: '12px 16px', fontSize: 16, width: '100%', background: '#f8f9fa', color: '#333', fontWeight: 400, boxSizing: 'border-box' }} />
            <div style={{display:'flex',justifyContent:'center',margin:'0 0 8px 0'}}>
              <span style={{color:'#223a5e',textDecoration:'underline',cursor:'pointer',fontSize:14}} onClick={handleForgotPassword}>
                ¿Has olvidado tu contraseña?
              </span>
            </div>
          </>
        )}
        <button type="submit" disabled={loading} style={{
          background: '#223a5e',
          color: '#fff',
          border: 'none',
          borderRadius: 8,
          padding: '14px 24px',
          fontWeight: 600,
          fontSize: 16,
          marginTop: 8,
          cursor: 'pointer',
          boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
          width: '100%',
          alignSelf: 'center',
          textAlign: 'center',
          transition: 'background 0.2s',
          fontFamily: '"Roboto Condensed", sans-serif'
        }}>
          {loading ? (isLogin ? 'Ingresando...' : 'Registrando...') : (isLogin ? 'Ingresar' : 'Registrarse')}
        </button>
  {error && <div style={{ color: '#dc3545', background: '#f8d7da', border: '1px solid #f5c6cb', borderRadius: 6, padding: '8px 12px', textAlign: 'center', fontWeight: 400, fontSize: 14, width: '100%', boxSizing: 'border-box' }}>{error}</div>}
  {mensaje && <div style={{ color: '#155724', background: '#d4edda', border: '1px solid #c3e6cb', borderRadius: 6, padding: '8px 12px', textAlign: 'center', fontWeight: 400, fontSize: 14, width: '100%', boxSizing: 'border-box' }}>{mensaje}</div>}
        <div style={{ textAlign: 'center', marginTop: 8 }}>
          {isLogin ? '¿No tienes cuenta?' : '¿Ya tienes cuenta?'}{' '}
          <span style={{ color: '#e6c86b', cursor: 'pointer', textDecoration: 'underline' }} onClick={() => setIsLogin(!isLogin)}>
            {isLogin ? 'Regístrate' : 'Inicia sesión'}
          </span>
        </div>
      </form>
    </div>
  );
}

export default LoginRegistro;
