import React from 'react';
import { Link, useLocation } from 'react-router-dom';

// Estilos globales para fondo y fuente
if (typeof document !== 'undefined') {
  document.body.style.background = '#e6eaf2'; // gris azulado suave
  document.body.style.fontFamily = 'Segoe UI, Roboto, Arial, Helvetica, sans-serif';
  document.body.style.margin = 0;
}

function Navbar() {
  const location = useLocation();
  const links = [
    { to: '/', label: 'INICIO' },
    { to: '/publicar', label: 'PUBLICAR' },
    { to: '/aviso-legal', label: 'LEGAL' },
    { to: '/contacto', label: 'GU√çA' }
  ];

  // Colores militares y dorado
  const verdeMilitar = 'linear-gradient(90deg, #4b5d3a 0%, #6b7b4c 100%)';
  const doradoMilitar = 'linear-gradient(90deg, #bfa14a 0%, #e6c86b 100%)';
  // Usar el color directamente

  return (
    <nav style={{
      background: '#223a5e',
      color:'#fff',
      padding:'0',
      margin:0,
      width:'100vw',
      minWidth:'100vw',
      position:'fixed',
      left:0,
      top:0,
      zIndex:100,
      boxShadow:'0 2px 8px #0002',
      fontFamily: 'Segoe UI, Roboto, Arial, Helvetica, sans-serif'
    }}>
      <div
        style={{
          maxWidth: 1200,
          margin: '0 auto',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between',
          height: 64,
          flexDirection: 'row',
        }}
      >
        <div
          style={{
            display: 'flex',
            alignItems: 'center',
            gap: 10,
            width: 'auto',
            '@media (max-width: 700px)': {
              width: '100%',
              justifyContent: 'center',
              flexDirection: 'column',
              marginBottom: 8,
            },
          }}
        >
          <span
            style={{
              fontWeight: 900,
              fontSize: 28,
              letterSpacing: 2,
              color: '#fff',
              textShadow: '1px 1px 2px #111',
              fontFamily: 'inherit',
              '@media (max-width: 700px)': {
                fontSize: 22,
                marginBottom: 8,
              },
            }}
          >
            VENTADEARMAS
          </span>
          <span style={{ fontSize: 22, marginLeft: 8 }}>üá¶üá∑</span>
        </div>
        <div
          style={{
            display: 'flex',
            flexDirection: 'row',
            gap: 12,
            alignItems: 'center',
            justifyContent: 'flex-end',
            width: '100%',
            '@media (max-width: 700px)': {
              flexDirection: 'row',
              justifyContent: 'center',
              gap: 8,
              marginTop: 8,
            },
          }}
        >
          {links.map(link => (
            <Link
              key={link.to}
              to={link.to}
              style={{
                textDecoration: 'none',
                fontWeight: 700,
                fontSize: 16,
                padding: '6px 12px',
                borderRadius: 8,
                background: location.pathname === link.to ? doradoMilitar : verdeMilitar,
                color: location.pathname === link.to ? '#222' : '#fff',
                border: location.pathname === link.to ? '2px solid #bfa14a' : '2px solid transparent',
                transition: 'all 0.2s',
                letterSpacing: 1,
                cursor: 'pointer',
                marginLeft: 0,
                minWidth: 60,
                textAlign: 'center',
                '@media (max-width: 700px)': {
                  fontSize: 13,
                  padding: '4px 6px',
                  minWidth: 48,
                },
              }}
            >
              {link.label}
            </Link>
          ))}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
